import React ,{useState} from 'react'
import '../Home.css';
import 'bootstrap/dist/css/bootstrap.min.css';

//import calcula from "../images/calculadora.png";



function calcular(){



    

    const qtdUsers = document.getElementById("qtdUsers").value;
    const valorUsers = document.getElementById("valorUsers").value;
    const conversao = document.getElementById("conversao").value;

    if (qtdUsers == "" || valorUsers == "" || conversao == "") {
        return false;
    }

    const usuariosComprados = ( parseInt(conversao) / 100 ) * parseInt(qtdUsers)

    const resultadoCompra = usuariosComprados * parseInt(valorUsers)


    document.getElementById("resultado").innerHTML = resultadoCompra;
    
    return (
    <div class="container " style={{marginTop:'80px'}}>          
        <div class="row">
                quantidade de usuários:
                <input type="text" id="qtdUsers" onchange="calcular()"/>

                valor em reais R$ do token por usuário:
                <input type="text" id="valorUsers" onchange="calcular()"/>

                Conversão de compra %:
                <input type="text" id="conversao" onchange="calcular()"/>

                R$ <label id="resultado">0</label>

                <br/>

                <button onclick="calcular()">calcular</button>
        </div>        
    </div>
        
    );
}

export default calcular;
