import React ,{useState} from 'react'
import '../Home.css';
import 'bootstrap/dist/css/bootstrap.min.css';

//import calcula from "../images/calculadora.png";



function Calcular(){

    const [qtdUsers, setSubtotal] = useState(0);
    const [valorUsers, setNumDiners] = useState(0);
    const [conversao, setTipPercetnage] = useState(0);

    const submit = (e) => {
        e.preventDefault();

    if (qtdUsers == "" || valorUsers == "" || conversao == "") {
        return false;
    }

    const usuariosComprados = ( parseInt(conversao) / 100 ) * parseInt(qtdUsers)

    const resultadoCompra = usuariosComprados * parseInt(valorUsers)


    document.getElementById("resultado").innerHTML = resultadoCompra;
    };
    
    return (
    <div class="container " style={{marginTop:'80px'}}>          
        <div class="row">
       


      quantidade de usuários:
    <input type="text" id="qtdUsers" onchange="calcular()"/>

    valor em reais R$ do token por usuário:
    <input type="text" id="valorUsers" onchange="calcular()"/>

    Conversão de compra %:
    <input type="text" id="conversao" onchange="calcular()"/>

    R$ <label id="resultado">0</label>

    <br/>

    <button onclick="calcular()">calcular</button>




    <form onSubmit={submit}>
          <fieldset>
            <label>subtotal</label>
              <input
                  value={qtdUsers}
                  onChange={(e) => setSubtotal(e.target.value)}
                />
          </fieldset>
          <fieldset>
            <label>number of people sharing the bill</label>
              <input
                  value={numDiners}
                  onChange={(e) => setNumDiners(e.target.value)}
                />
            </fieldset>
          <fieldset>
            <label>tip percentage</label>
              <select
                  value={tipPercentage}
                  onChange={(e) => setTipPercetnage(e.target.value)}
                >
                <option value="0">0%</option>
                <option value="0.05">5%</option>
                <option value="0.1">10%</option>
                <option value="0.15">15%</option>
                <option value="0.2">20%</option>
              </select>
          </fieldset>
      <button type="submit">Calculate</button>
            </form>
           
            
        </div>        
    </div>
        
    );
}

export default Calcular;
